# Replace all variations of missing data with NaN
flights.replace(['', ' ', None, 'missing', 'NA', 'NaN'], np.nan, inplace=True)

# Reassign filled values instead of using inplace
flights['minutes_delayed_carrier'] = flights['minutes_delayed_carrier'].fillna(flights['minutes_delayed_carrier'].median())
flights['minutes_delayed_nas'] = flights['minutes_delayed_nas'].fillna(flights['minutes_delayed_nas'].median())
flights['year'] = flights['year'].ffill()

# Export one row of the data to JSON to show NaN values
